  --accent-foreground: 220 10% 20%;

  --destructive: 0 72% 51%;

  --destructive-foreground: 0 0% 98%;

  --input: 220 15% 75%;
  --ring: 280 75% 55%;
  --chart-1: 280 75% 55%;
  --chart-2: 170 65% 45%;
  --chart-3: 210 70% 50%;
  --chart-4: 330 65% 55%;
  --chart-5: 45 80% 55%;

  --font-sans: Inter, system-ui, -apple-system, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --font-display: Plus Jakarta Sans, Inter, sans-serif;
  --radius: .5rem; /* 8px */
  --shadow-2xs: 0px 1px 2px 0px hsl(220 15% 15% / 0.05);
  --shadow-xs: 0px 1px 3px 0px hsl(220 15% 15% / 0.10);
  --shadow-sm: 0px 2px 4px -1px hsl(220 15% 15% / 0.06), 0px 1px 2px -1px hsl(220 15% 15% / 0.10);
  --shadow: 0px 4px 6px -1px hsl(220 15% 15% / 0.08), 0px 2px 4px -2px hsl(220 15% 15% / 0.10);
  --shadow-md: 0px 6px 12px -2px hsl(220 15% 15% / 0.10), 0px 3px 6px -3px hsl(220 15% 15% / 0.12);
  --shadow-lg: 0px 10px 20px -4px hsl(220 15% 15% / 0.12), 0px 6px 12px -6px hsl(220 15% 15% / 0.14);
  --shadow-xl: 0px 20px 30px -8px hsl(220 15% 15% / 0.14), 0px 10px 20px -10px hsl(220 15% 15% / 0.16);
  --shadow-2xl: 0px 30px 50px -12px hsl(220 15% 15% / 0.18);
  --tracking-normal: 0em;
  --spacing: 0.25rem;

  /* Automatically computed borders - intensity can be controlled by the user by the --opaque-button-border-intensity setting */

  /* Fallback for older browsers */
